package lab3;

public class exercise6 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.print("1. ");
		for(int i=1;i<=5;i++)
		{
			for(int j=1;j<i+1;j++)
			{
				System.out.print(i);
			}
		}
		
		System.out.println();
		
		System.out.print("2. ");
		for(int i=2;i<=6;i++)
		{
			for(int j=1;j<i+1;j++)
			{
				System.out.print(i);
			}
		}
		
		
		System.out.println();
		
		System.out.print("3. ");
		for(int i=1;i<=5;i=i+2)
		{
			for(int j=1;j<i+1;j++)
			{
				System.out.print(i);
			}
		}
		
		
		System.out.println();
		
		System.out.print("4. ");
		for(int i=1;i<=7;i=i+2)
		{
			for(int j=1;j<i+1;j=j+2)
			{
				System.out.print(i);
			}
		}
		
		
		System.out.println();
		
		System.out.print("5. ");
		for(int i=5;i>0;i--)
		{
			for(int j=1;j<i+1;j++)
			{
				System.out.print(i);
			}
		}
		
		
		System.out.println();
		
		System.out.print("6. ");
		for(int i=5;i>0;i--)
		{
			for(int j=7;j>i+1;j--)
			{
				System.out.print(i);
			}
		}
		
		
		System.out.println();
		
		System.out.print("7. ");
		for(int i=1;i<=5;i=i+2)
		{
			for(int j=1;j<i+1;j=j+1)
			{
				System.out.print('+');
			}
			for(int k=1;k<=i+1;k=k+1)
			{
			System.out.print('*');
			}
		}
		
		
		System.out.println();
		
		System.out.print("8. ");
		for(int i=2;i<=5;i=i+3)
		{
			for(int j=1;j<i+1;j=j+1)
			{
				System.out.print('-');
			}
			for(int k=1;k<=i+1;k=k+1)
			{
			System.out.print('*');
			}
			for(int l=1;l<=i+2;l=l+1)
			{
			System.out.print('+');
			}
		}
		
		
		System.out.println();
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		}		
}
